import React from "react";

export default function AddButton(){
  const [count, setCount] = React.useState(1)




  const StockButton =(({ text, stock, initial,handleOnClick})=>{
      /* --------------------- Funci贸n para restar contador --------------------- */
      const onAdd = ({handleOnClick}) =>{
        if(count < stock){
          setCount(count + 1)
        }

      }

      /* --------------------- Funci贸n para aumentar contador --------------------- */
      const onRest = ({handleOnClick}) =>{
        if(count >1){
          setCount(count -1)
        }
      }

    return(
    <button className="stock-button" onClick={handleOnClick}>
      {text}
    </button>
    )
  })

  const AddButton = ()=>{
    return(
      <button className="boton-agregar">
        Agregar al carrito
      </button>
    )
  }

  return(
    <>
    <div className="contenedor-agregar">
      <StockButton text="-" handleOnClick={onRest}/>
      <span className="add-button-count">{count}</span>
      <StockButton text="+" handleOnClick={onAdd}/>
    </div>
    <AddButton />
    </>
  )

}
