import React from "react";

export default function AddButton(){
  const [count, setCount] = React.useState(1)

  /* --------------------- Funci贸n para restar contador --------------------- */
  const onAdd = () =>{
    setCount(count + 1)
  }

  /* --------------------- Funci贸n para aumentar contador --------------------- */
  const onRest = () =>{
    if(count >1){
      setCount(count -1)
    }
  }

  const Stock =({handleOnClick, text})=>{
    return(
    <button className="stock-button">
      {text}
    </button>
    )
  }

  const AddButton = ()=>{
    return(
      <button className="boton-agregar">
        Agregar al carrito
      </button>
    )
  }

  return(
    <div className="contenedor-agregar">
      <Stock text="-" handleOnClick={onRest}/>
      <AddButton />
      <Stock text="+" handleOnClick={onAdd}/>
    </div>
  )

}
